echo "/* Creator Link Styles */
.creator-link {
    color: var(--primary-color);
    text-decoration: none;
    font-family: 'Courier New', monospace;
    font-size: 1.1rem;
    padding: 10px 20px;
    border: 1px solid var(--primary-color);
    border-radius: var(--border-radius);
    display: inline-block;
    transition: all 0.3s ease;
    background: rgba(88, 166, 255, 0.1);
    margin-top: 10px;
}

.creator-link:hover {
    background: var(--primary-color);
    color: var(--bg-color);
    box-shadow: 0 0 15px var(--primary-color);
    transform: translateY(-2px);
}
" >> d:\code\styles.css

echo "
/* Mobile Responsiveness */
@media (max-width: 768px) {
    body {
        padding: 10px;
    }

    header {
        flex-direction: column;
        align-items: flex-start;
        gap: 15px;
    }

    header > div {
        width: 100%;
        justify-content: space-between;
        flex-wrap: wrap;
    }

    header h1 {
        font-size: 1.2rem;
    }

    .main-nav {
        margin-left: 0;
        width: 100%;
        overflow-x: auto;
        padding-bottom: 5px;
        gap: 10px;
        white-space: nowrap;
        -webkit-overflow-scrolling: touch;
    }

    .nav-link {
        padding: 5px 8px;
        font-size: 0.9rem;
    }

    .full-screen-layout {
        grid-template-columns: 1fr;
        height: auto;
        gap: 15px;
    }

    .editor-section, .files-section {
        padding: 15px;
        height: auto;
        min-height: 500px;
    }

    .controls-row {
        flex-direction: column;
        align-items: stretch !important;
        gap: 10px;
    }

    .form-group {
        width: 100%;
    }

    #aiGenerateBtn {
        width: 100%;
        justify-content: center;
    }

    .modal-content {
        width: 95%;
        margin: 20% auto;
    }

    .contact-links {
        flex-direction: column;
        align-items: center;
        gap: 15px;
    }

    .contact-link {
        width: 100%;
        max-width: 100%;
        flex-direction: row;
        justify-content: center;
        padding: 15px;
    }

    .chat-section {
        height: calc(100vh - 200px) !important;
    }

    .chat-bubble {
        max-width: 90%;
    }

    /* Hide font controls on very small screens if needed, or adjust */
    .font-controls {
        display: none !important;
    }

    .theme-selector {
        flex-grow: 1;
    }

    .theme-selector select {
        width: 100%;
    }
}
" >> d:\code\styles.css
